INSERT INTO `downloads` VALUES (1,'Kalender 2018','Tersedia Secara Gratis Kalender Event Dinas Pariwisata Kota Medan.','https://drive.google.com/open?id=144zW_erU3BdCv9scaGgN6scmZ0cQ8Gro','1544705211_kal.png',1544705211,1544705211),(2,'Heritage and Cullinary','Bangunan Peninggalan Lama dan kuliner yang ada dikota Medan akan dibahas dalam buku ini.','https://drive.google.com/file/d/0Bwo4J9g4dc1JWURNRW1mM1dFa00/view?usp=drive_web','1544705235_booklet11.png',1544705235,1544705235),(3,'Medan Heritage','Bangunan Peninggalan Lama yang ada dikota Medan akan dibahas dalam buku ini.','https://drive.google.com/file/d/0Bwo4J9g4dc1JLWU5TExkanZCSVU/view','1544705271_bookk.png',1544705271,1544705271),(4,'Tourism Guide Booklet','Medan mempunyai banyak tempat wisata dan kuliner yang sangat beragam dengan membaca buku ini kita dapat mengenal lebih kota Medan.','https://drive.google.com/file/d/0Bwo4J9g4dc1JS3k5TzJuNXBWX1k/view','1544705306_booklet.png',1544705306,1544705306),(5,'Bahan Promosi MTQ Nasional XXVIII Tahun 2018','Mari Kita Sukseskan Musabaqah Tilawatil Quran Nasional XXVII Tahun 2018 Bertempat di Kota Medan dan Kabupaten Deli Serdang Provinsi Sumatera Utara pada Tanggal 04 - 13 Oktober 2018.','http://bit.ly/2MHuYJB','1544705323_mtqsepanduk.png',1544705323,1544705323);
